<template>
  <nav-link v-if="isLink" :class="classes" :to="to"><slot></slot></nav-link>
  <button v-else :class="classes" type="button" @click="handleClick">
    <span v-if="loading" class="btn__loading-wrapper">
      <img
        src="data:image/svg+xml;base64,"
        alt=""
      />
    </span>
    <slot></slot>
  </button>
</template>

<script>
import NavLink from '../NavLink'

export default {
  name: 'Button',
  components: {
    NavLink,
  },
  props: {
    to: {
      type: String,
      default: '',
    },
    type: {
      type: String,
      default: 'default',
    },
    size: {
      type: String,
      default: 'default',
    },
    light: {
      type: Boolean,
      default: false,
    },
    loading: {
      type: Boolean,
      default: false,
    },
  },
  computed: {
    isLink() {
      return !!this.to
    },
    classes() {
      const { light, type, size, loading } = this

      return [
        'btn',
        `btn--${light ? 'light' : type}`,
        `btn--${size}`,
        {
          'btn--loading': loading,
        },
      ]
    },
  },
  methods: {
    handleClick() {
      this.$emit('click')
    },
  },
}
</script>

<style lang="stylus">
@import '../../styles/_variables.styl'

.btn
  position: relative
  display: inline-block
  padding: 1.5em 6em
  border: 2px solid $black
  font-size: 12px
  border-radius: 4px
  transition: border 0.2s, background 0.2s, color 0.2s ease-out

  &:hover
    color: $black
    background-color: $white
    cursor: pointer

  &--default
    color: $white
    background-color: $black

  &--light
    color: $black
    border-color: $white
    background-color: $white

    &:hover
      color: $white
      background-color: $black

  &--small
    padding: 1em 4em
  &--large
    font-size: 14px

  &--loading img
    width: 1em
    height: 1em

  &__loading-wrapper
    position: absolute
    top: 0
    left: 0
    display: flex
    width: 100%
    height: 100%
    justify-content: center
    align-items: center
    background-color: $black

    img
      animation: loadingCircle 1s infinite linear

@keyframes loadingCircle {
  from {
    transform-origin: 50% 50%
    transform: rotate(0deg)
  }

  to {
    transform-origin: 50% 50%
    transform: rotate(1turn)
  }
}
</style>
